mapper.update:
update question_info set question_type = #{questionType,jdbcType=VARCHAR}, question_content = #{questionContent,jdbcType=VARCHAR},
                        qnr_id= #{qNRId,jdbcType=VARCHAR}
where id = #{id,jdbcType=VARCHAR}

mapper.select:
SELECT id,question_type as "questionType", question_content as "questionContent", qnr_id as "qNRId"
FROM question_info
WHERE qnr_id= #{qNRId,jdbcType=VARCHAR}
